mapper.insert:

 		insert into tb_bo_inc_memo
			 ( bo_id
			   ,inc_yy
			   ,def_cl
			   ,pln_ret_cl
			   ,inc_itm_id
			   ,memo_mon
			   ,memo
			   ,memo_ver
			   ,chg_dt
			   ,reg_no
			   ,del_yn
			  )
			 values
	           ( 
	           	 #{boId}
	           	 ,#{incYy}
	           	 ,#{defCl}
	           	 ,#{plnRetCl}
	           	 ,#{incItmId}
	           	 ,#{memoMon}
	           	 ,#{memo}
	           	 ,if(#{memoId} is null or #{memoId} = '' ,1 , (select max(t.memo_ver) +1 from tb_bo_inc_memo t where t.memo_id = #{memoId}) )
	           	 ,NOW()
				 ,#{regNo}
				 ,'N'
			 )

mapper.update:

update tb_bo_inc_memo

	chg_dt = now()
	, upd_no = #{updNo}
	, del_yn = 'Y'			

where memo_id = #{memoId}
